DROP TABLE IF EXISTS trails;

CREATE TABLE trails (
   trail_id SERIAL PRIMARY KEY NOT NULL,
   trail_name varchar(40) NOT NULL,
   location varchar(40) NOT NULL,
   difficulty varchar(20) NOT NULL,
   distance numeric,
   description text,
   rating numeric,
   map_link text
);


INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Taft Point and The Fissures', 'Yosemite', 'Easy', 2.3, 'The view at Taft Point is an awe-inspiring sight of the granite cliffs dropping into the valley below. The adjacent Fissures trail then takes you through narrow and deep cracks in the granite.', 4.7, 'https://www.google.com/maps/place/Taft+Point+Trailhead/@37.7116754,-119.5903835,17z/data=!4m20!1m13!4m12!1m4!2m2!1d-71.155712!2d42.3985152!4e1!1m6!1m2!1s0x8096f26188311489:0xa5b6cf114c659813!2staft+point+trailhead!2m2!1d-119.5863257!2d37.7126007!3m5!1s0x8096f26188311489:0xa5b6cf114c659813!8m2!3d37.7126007!4d-119.5863257!16s%2Fg%2F1z44b9x_3?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Brink of the Lower Falls Trail', 'Yellowstone', 'Moderate', 0.7, 'Hiking through lush greenery within the canyon, catch glimpses of the blue waters of the Yellowstone River below and witness the majestic 308-foot waterfall at the brink of the Lower Falls.', 4.7, 'https://www.google.com/maps/place/Grand+Canyon+Of+The+Yellowstone:+North+Rim+Parking+Lot/@44.7196634,-110.4978585,20z/data=!4m6!3m5!1s0x534e21555afb3b5b:0xe4b44a1e4614b544!8m2!3d44.719698!4d-110.497531!16s%2Fg%2F11txh8jbn1?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('South Kaibab Trail to Cedar Ridge', 'Grand Canyon', 'Moderate', 3.1, 'This trail offers steep switchbacks, surrounded by color-shifting canyon walls depending on the sunlight. Cedar Ridge is a picturesque plateau with panoramic vistas of the sprawling canyon.', 4.9, 'https://www.google.com/maps/place/South+Kaibab+to+Cedar+Ridge/@36.0534046,-112.0848262,18z/data=!4m25!1m18!4m17!1m11!1m2!1s0x89e377720712c453:0x460671c168457298!2sCambridge,+MA+02138!2m2!1d-71.1389101!2d42.3803274!3m4!1m2!1d-92.7150195!2d36.986296!3s0x87cfebc3d60d084d:0xae52735e0c256064!1m3!2m2!1d-112.08363!2d36.05299!3e0!3m5!1s0x8733107a00e858a1:0x644a1ebd074940a1!8m2!3d36.0534047!4d-112.0835388!16s%2Fg%2F11c1p29ymg?entry=ttu');

INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Mud Volcano Trail', 'Yellowstone', 'Easy', 0.8, 'Explore the unique geothermal wonders of Yellowstone on the Mud Volcano Trail. Witness bubbling mud pots, steaming vents, and vibrant thermal features amidst stunning natural landscapes. ', 4.5, 'https://www.google.com/maps/place/Mud+Volcano+Trailhead/@44.623382,-110.4356994,17.25z/data=!4m15!1m8!3m7!1s0x534e1f2ee667bd4d:0xd6c9c90c33719464!2sMud+Volcano+Trl,+Wyoming+82190!3b1!8m2!3d44.6221102!4d-110.4335013!16s%2Fg%2F11txpg9vy3!3m5!1s0x534e1f2e72f8f073:0x9a1e8079110fe05f!8m2!3d44.6248638!4d-110.4338186!16s%2Fg%2F11ss74wk25?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Vernal Falls Trail', 'Yosemite', 'Hard', 3.1, 'Follow Mist Trail up a short but steep paved path reaching a bridge with views of Vernal Falls. Continue and you will climb a steep rock stair set up to the falls themselves (it can be slippery). ', 4.8, 'https://www.google.com/maps/place/Mist+Trail+%26+John+Muir+Trail+Trailhead/@37.7307997,-119.5657191,16z/data=!4m9!1m2!2m1!1svernal+falls+trail+yosemite!3m5!1s0x8096f24cafc0eb81:0xa9cbf1a15590ed4f!8m2!3d37.7328706!4d-119.5577371!16s%2Fg%2F11c2plh7gk?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Hidden Lake Overlook Trail', 'Glacier', 'Moderate', 2.7, 'Here you will be standing between the towering Clements Mountain and the jagged ridgeline of Reynolds Mountain overlooking Hidden Lake. See open grasslands and mountains along the way.', 4.7, 'https://www.google.com/maps/place/Logan+Pass+Visitor+Center/@48.6946033,-113.7234943,16z/data=!3m1!5s0x5368af512bc4b521:0x815402de1f2424db!4m14!1m7!3m6!1s0x5368a8a64a946cdd:0x447d85b782e4267f!2sHidden+Lake+Overlook!8m2!3d48.6872611!4d-113.7416209!16s%2Fg%2F11bwnblv56!3m5!1s0x5368af512bf83b1f:0x64cdf1479b75bbbd!8m2!3d48.695291!4d-113.7177915!16s%2Fm%2F04yfp25?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Hall of Mosses Trail', 'Olympic', 'Easy', 1.1, 'Wander through a wonderland of moss, trees, and ferns - including an iconic moss-draped archway. This short and sweet loop is a busy trail through lush Washington temperate rainforest.', 4.6, 'https://www.google.com/maps/place/Hall+of+Mosses+Trailhead/@47.8614837,-123.9367415,17z/data=!4m14!1m7!3m6!1s0x548e6a766fd2c6af:0x407b4880146f1070!2sHall+of+Mosses+Trailhead!8m2!3d47.8614801!4d-123.9341666!16zL20vMGdxanAz!3m5!1s0x548e6a766fd2c6af:0x407b4880146f1070!8m2!3d47.8614801!4d-123.9341666!16zL20vMGdxanAz?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('The Loch via Glacier Gorge Trail', 'Rocky Mountain', 'Moderate', 5.4, 'This trail features views of the Eastern Glacier Knobs, Taylor Glacier, and Haiyaha Lake. At the end, you will find The Loch, a tranquil alpine lake nestled in the heart of the Rockies.', 4.8, 'https://www.google.com/maps/place/Glacier+Gorge+Trailhead/@39.5277212,-106.3696468,4z/data=!4m19!1m12!4m11!1m6!1m2!1s0x89e377720712c453:0x460671c168457298!2sCambridge,+MA+02138!2m2!1d-71.1389101!2d42.3803274!1m3!2m2!1d-105.64032!2d40.31048!3m5!1s0x87697b75682e87fd:0x9dbcb928991ae04f!8m2!3d40.310403!4d-105.640362!16s%2Fg%2F11g_24dbb?entry=ttu');
INSERT INTO trails (trail_name, location, difficulty, distance, description, rating, map_link) VALUES ('Angels Landing Trail', 'Zion', 'Hard', 4.3, 'This strenuous trail in Zion National Park has steep drop-offs and very narrow sections. However, the technical route and incredible views of Zion Canyon make this hike the most popular in Zion.', 4.9, 'https://www.google.com/maps/place/Angels+Landing+Trail/@38.4109631,-110.0551215,4z/data=!4m19!1m12!4m11!1m6!1m2!1s0x89e377720712c453:0x460671c168457298!2sCambridge,+MA+02138!2m2!1d-71.1389101!2d42.3803274!1m3!2m2!1d-112.95122!2d37.2591!3m5!1s0x80cac311cf4d2235:0xa63037aa4b232e4d!8m2!3d37.2592428!4d-112.9512609!16s%2Fg%2F11h10_bjtl?entry=ttu');
